import React from 'react';

interface bbox{
	bbox: number[],
	category:string
}
interface segment{
	segments: number[][]
}
interface segments{
	area: number,
	segments:segment,
	category:string
}



interface props{
	filename: string,
	url: string,
	height: number,
	width: number,
	info: {
		bboxes: bbox[],
		segments: segments

	}
}

export class DrawCOCOimage extends React.Component<any, any>{
	public canvasRef = React.createRef<any>();
	public canvas:any;
	constructor(props: any) {
		super(props);

	}

	componentDidMount() {
		//console.log(this.props.props.Image.filename );
		const imageObj1 = new Image();
		imageObj1.src = this.props.props.Image.url;
		imageObj1.onload = ()=> {
			const ctx = this.canvas.getContext('2d');
			ctx.drawImage(imageObj1,0,0);

			
		};
	}


	render() {
		return (
			<div>
				<canvas ref={(canvas)=>this.canvas=canvas} width={300} height={300}> </canvas>
			</div>
		);

	}
}
