import React, {useContext} from 'react';
import {backgroundParticlesConfig} from './configs/particlesConfig';
import {Container} from 'tsparticles/dist/Core/Container';
import {modalStateInterface, NIGHT, DAY} from '../interfaces/stateInterfaces';
import Particles from 'react-tsparticles';

interface properties {
	properties: modalStateInterface,
	theme: string
}


class BackGroundParticles extends React.Component<properties>{

	public particlesContainer: Container | undefined;
	private theme:string;
	public options:any;
	constructor(properties:properties) {
		super(properties);
		// this.particlesInit = this.particlesInit.bind(this);
		this.particlesLoaded = this.particlesLoaded.bind(this);
		this.theme = properties.theme;
		this.particlesContainer;
		this.options = backgroundParticlesConfig;
	}

	componentDidMount() {
		//
	}

	// particlesInit(main:any) {
	// 	console.log(main);
	// }

	particlesLoaded(container:any) {
		this.particlesContainer = container;
		//console.log(this.particlesContainer);
	}


	pause(theme: string){

		this.options.particles.color.value = (theme === NIGHT)? '#ffffff': '#141414';
		this.options.particles.line_linked.color = (theme === NIGHT)? '#ffffff': '#141414';
		this.particlesContainer?.pause();
		
	}
	resume(){

		this.particlesContainer?.play();
	}

	changeTheme(theme: string){
		console.log(theme);
		this.options.particles.color.value = (theme === NIGHT)? '#ffffff': '#141414';
		this.options.particles.line_linked.color = (theme === NIGHT)? '#ffffff': '#141414';
		// this.options.particles.color.value = (theme === NIGHT)? '#141414': '#ffffff';
		// this.options.particles.line_linked.color = (theme === NIGHT)? '#141414': '#ffffff';
		//this.forceUpdate();
		// this.particlesContainer?.options.load(JSON.parse(JSON.stringify(this.options)));
		//
		// this.particlesContainer?.refresh();
		// this.particlesContainer?.destroy();
		// this.particlesContainer?.start();
		//console.log(this.particlesContainer);
	}

	componentDidUpdate(prevProps: Readonly<properties>) {
		// console.log(this.particlesContainer);
		// this.props.theme==='day'? this.changeTheme(): this.changeTheme();
		if(prevProps.theme!==this.props.theme) this.changeTheme(this.props.theme);
		Object.values(this.props.properties).includes(true)? this.pause(this.props.theme): this.resume();
	}



	render(){
		return(
			<Particles loaded={this.particlesLoaded} params={

				{

					particles: {
						number: {
							value: 90,
							density: {
								enable: true,
								value_area: 800
							}
						},
						color: {
							value: (this.props.theme === NIGHT)? '#ffffff': '#141414',
						},
						shape: {
							type: 'circle',
							stroke: {
								width: 0,
								color: '#000000'
							},
							polygon: {
								nb_sides: 3
							},
							image: {
								src: 'img/github.svg',
								width: 100,
								height: 100
							}
						},
						opacity: {
							value: 0.0,
							random: false,
							anim: {
								enable: false,
								speed: 1,
								opacity_min: 0.7,
								sync: false
							}
						},
						size: {
							value: 1,
							random: true,
							anim: {
								enable: false,
								speed: 40,
								size_min: 0.1,
								sync: false
							}
						},
						line_linked: {
							enable: true,
							distance: 250,
							color: (this.props.theme === NIGHT)? '#ffffff': '#141414',
							opacity: 0.3,
							width: 1.5
						},
						move: {
							enable: true,
							speed: 0.5,
							direction: 'left',
							random: false,
							straight: false,
							out_mode: 'out',
							bounce: false,
							attract: {
								enable: false,
								rotateX: 600,
								rotateY: 1200
							}
						}
					},
					interactivity: {
						detect_on: 'canvas',
						events: {
							onhover: {
								enable: false,
								mode: 'grab'
							},
							onclick: {
								enable: false,
								mode: 'push'
							},
							resize: true
						},
						modes: {
							grab: {
								distance: 140,
								line_linked: {
									opacity: 1
								}
							},
							bubble: {
								distance: 400,
								size: 40,
								duration: 2,
								opacity: 8,
							},
							repulse: {
								distance: 200,
								duration: 0.4
							},
							push: {
								particles_nb: 4
							},
							remove: {
								particles_nb: 2
							}
						}
					}

				}

			}/>
		);
	}
}

export const MemorizedBackgroundParticles = React.memo(BackGroundParticles);
