import React from 'react';

export class DrawCOCOimage extends React.Component<any, any>{
	public canvasRef = React.createRef();
	constructor(props: any) {
		super(props);

	}

	componentDidMount() {
		const ctx = this.canvasRef.current.canvas.getContext('2d');
		const imageObj1 = new Image();
		imageObj1.src = this.props.filename;
		imageObj1.onload = function() {
			ctx.drawImage(imageObj1,0,0);
		};
	}


	render() {
		return (
			<div>
				<canvas ref={this.canvasRef} width={300} height={300}> </canvas>
			</div>
		);

	}
}
