import React, {useState} from 'react';
import { Carousel } from 'react-bootstrap';
import Meta from '../meta.json';
import ProjectsContext from './ProjectsContext';

export default function CarouselOfProjects(){

	const rightArrowRef = React.createRef() as React.MutableRefObject<HTMLInputElement>;
	const leftArrowRef = React.createRef() as React.MutableRefObject<HTMLInputElement>;
	const [projectsStatus, setStatus] = useState({
		textRecognition: false,
		arraySort: false,
		custom: false
	});


	function emitSlideOnWheel(event: React.WheelEvent){
		(event.deltaY/100>0) ?  rightArrowRef.current.click() : leftArrowRef.current.click();
	}

	const style ={
		inlineFlex:{
			display:'table-row-group',
		},
		zIndex10:{
			transform: 'translate(-0%,0%)',
			zIndex:1,
		},
	};
	return(
		<ProjectsContext.Provider>
			<div className="NetworkOfProjects_div">
				<Carousel nextIcon={<span ref={rightArrowRef} aria-hidden="true" className="carousel-control-next-icon"/>}
					prevIcon={<span ref={leftArrowRef} aria-hidden="true" className="carousel-control-prev-icon"/>}
					interval={15000} 
					style={style.inlineFlex} 
					onSlide={()=>console.log('ttteesstt')}>

					{
						Meta.data.projects.map((item,it)=>{
							return <Carousel.Item key={it}>
								<img
									className="d-block w-100"
									src={process.env.PUBLIC_URL+item.background}
									alt={item.name + 'background'}
								/>
								<img onWheel={ emitSlideOnWheel }
									onClick={() => setStatus({
										textRecognition: true,
										arraySort: false,
										custom: false
									})}
									className="d-block w-100 foreground"
									src={process.env.PUBLIC_URL+item.foreground}
									alt={item.name + 'foreground'}
								/>

								<Carousel.Caption style={style.zIndex10}>
									<h3>{item.name}</h3>
									<p>Nulla vitae elit libero, a pharetra augue mollis interdum.</p>
								</Carousel.Caption>
							</Carousel.Item>;
						})
					}

				</Carousel>
			</div>
		</ProjectsContext.Provider>
	);
}
