import React,{Component} from 'react';
import {Accordion, Badge, Card, Col, Form, Button} from 'react-bootstrap';
import {classes, parseSubCategories} from './cocoParser';
import {Typeahead} from 'react-bootstrap-typeahead';

interface subClassesStateInterface {
	subclasses: string[],
	badges: string[];
}

export interface filterData{
	superclass: string,
	subclasses: string[],
	image: {
		width: number,
		height: number
	},
	bboxes:{
		maxWidth: number,
		maxHeight: number,
		minWidth: number,
		minHeight: number
	},
	segments:{
		maxSquare: number,
		minSquare: number
	},
	path: string,
	only: number
}

type callbackProp = {
	callback: (data: filterData)=>void;
}

export class ClassesCOCO extends Component<callbackProp>{
	public state: subClassesStateInterface;
	public superCategoriesRef = React.createRef<any>();
	private maxImageWidth = React.createRef<any>();

	constructor(props:callbackProp) {
		super(props);
		this.state = {subclasses: [''], badges: []};
	}


	componentDidMount() {
		//console.log(parseSubCategories('all'));
		this.setState({subclasses: parseSubCategories('all'), badges: []});
	}

	appendSubclasses(){
		const superClassValue = this.superCategoriesRef.current.value;
		this.setState({subclasses: parseSubCategories(superClassValue), badges: []});
	}
	appendBadges(e:string[]){
		const badge = e[0];
		const prevBadges = this.state.badges;
		if(!prevBadges.includes(badge) && badge!==undefined) {
			prevBadges.push(badge);
			const prevState = this.state;
			this.setState({subclasses: prevState.subclasses, badges: prevBadges});
		}
		console.log(this.state.badges);
	}

	removeBadge(e:any){
		e.preventDefault();
		const prevState = this.state;
		prevState.badges = prevState.badges.filter(item => item !== e.currentTarget.textContent);
		//console.log(e.currentTarget.textContent);
		this.setState(prevState);
	}


	filterPreview=(e:any)=>{

		e.preventDefault();
		const formData = new FormData(e.target),
			formDataObj = Object.fromEntries(formData.entries())
		console.log(formDataObj);
		
		const Filter = {
			superclass: this.superCategoriesRef.current.value,
			subclasses: this.state.badges,
			image: {
				width: 0,
				height: 0
			},
			bboxes:{
				maxWidth: 0,
				maxHeight: 0,
				minWidth: 0,
				minHeight: 0
			},
			segments:{
				maxSquare: 0,
				minSquare: 0
			},
			path: 'C:\\',
			only:3
		};

		console.log(Filter);
		this.props.callback(Filter);

	}


	render(){

		return(


			<Accordion defaultActiveKey="0">
				<Card>
					<Accordion.Toggle as={Card.Header} eventKey="0">
						Classes
					</Accordion.Toggle>
					<Accordion.Collapse eventKey="0">
						<Card.Body>
							<Form>
								<Form.Group controlId="formBasicClasses">
									<p>Superclasses: </p>
									<Form.Control size="sm" as="select" ref={this.superCategoriesRef} onChange={(e)=>{ this.appendSubclasses();} }>
										<option value='all'>all</option>
										{
											Object.keys(classes).map((item:any,it:number)=>{

												return <option key={it+1} value={item}>{item}</option>;

											})
										}
										)
									</Form.Control>
									<p></p>
									<p>Subclasses: </p>

								</Form.Group>

								<Typeahead
									id="toggle-example"
									options={this.state.subclasses}
									onChange={(e)=>{ this.appendBadges(e);}}
									placeholder="Subclasses...">
								</Typeahead>
								<br></br>
								{
									this.state?.badges.map((item,it)=>{

										return <Badge style={{marginLeft:'3px'}} onClick={(e:any)=>{this.removeBadge(e); }} key={it} variant="success">{item}</Badge>;
									})

								}

								<br></br>
								<br></br>
								<br></br>
								<br></br>
								<br></br>
								<br></br>
								<br></br>
								<br></br>
								<br></br>
								<br></br>
								<br></br>
								<br></br>
								<br></br>
								<br></br>
								<br></br>
							</Form>
						</Card.Body>
					</Accordion.Collapse>
				</Card>
				<Card>
					<Accordion.Toggle as={Card.Header} eventKey="1">
						Filters
					</Accordion.Toggle>
					<Accordion.Collapse eventKey="1">
						<Card.Body>
							<h6>Image properties:</h6>
							<br></br>

							<Form onSubmit={ event => this.filterPreview(event) }>
								<Form.Row>
									<p style={{marginLeft: '1rem'} }>Max image size:</p>
									<Col>
										<Form.Control size="sm" placeholder="width" />
									</Col>
									x
									<Col>
										<Form.Control size="sm" placeholder="height" />
									</Col>
								</Form.Row>
								<br></br>
								<h6 >Bounding boxes properties:</h6>
								<br></br>
								<Form.Row>
									<p style={{marginLeft: '1rem'} }>Max bounding box size:</p>
									<Col>
										<Form.Control size="sm" placeholder="width" />
									</Col>
									x
									<Col>
										<Form.Control size="sm" placeholder="height" />
									</Col>
								</Form.Row>

								<Form.Row>
									<p style={{marginLeft: '1rem'} }>Min bounding box size:</p>
									<Col>
										<Form.Control size="sm" placeholder="width" />
									</Col>
									x
									<Col>
										<Form.Control size="sm" placeholder="height" />
									</Col>
								</Form.Row>
								<br></br>
								<h6>Segments properties:</h6>
								<br></br>
								<Form.Row>
									<p style={{marginLeft: '1rem'} }>Max segments square:</p>
									<Col>
										<Form.Control size="sm" placeholder="square" />
									</Col>
								</Form.Row>

								<Form.Row>
									<p style={{marginLeft: '1rem'} }>Min segments square:</p>
									<Col>
										<Form.Control size="sm" placeholder="square" />
									</Col>
								</Form.Row>
								<Form.Row>

									<Button type="submit" variant="success" block>Filter</Button>

								</Form.Row>

							</Form>

						</Card.Body>
					</Accordion.Collapse>
				</Card>


				<Card>
					<Accordion.Toggle as={Card.Header} eventKey="2">
						Export
					</Accordion.Toggle>
					<Accordion.Collapse eventKey="2">
						<Card.Body>
							<h6>Export properties:</h6>
							<Form>
								<br></br>
								<Form.Row>
									<p style={{marginLeft: '1rem'} }> Max images:</p>
									<Col>
										<Form.Control pattern="[1-9]" size="sm" placeholder="number of images" />
									</Col>
								</Form.Row>

								<Form.Row>
									<Form.Check style={{marginLeft: '3rem'} }
										type="switch"
										id="voc-switch"
										label="Export to VOC format"
									/>
								</Form.Row>
								<br></br>
								<Form.Row>
									<Button variant="success" block>Export</Button>
								</Form.Row>

							</Form>

						</Card.Body>
					</Accordion.Collapse>
				</Card>
			</Accordion>

		);

	}
}
