import React from 'react';
import * as instancesVal from '../instances_val2017.json';


const parseCategories=()=> {
	const classesTemp:any = [];
	(<any>instancesVal).categories.forEach((item: any) => {

		if (!(item.supercategory in classesTemp)) {
			classesTemp[item.supercategory] = [];
		}
		if (!(item.name in classesTemp[item.supercategory])) {
			classesTemp[item.supercategory].push(item.name);
		}
	});
	return classesTemp;
};
export const classes = parseCategories();

export const parseSubCategories = (supercategory: string)=>{

	if(supercategory==='all') {
		console.log('qqqq');
		console.log(Object.values(classes).flat());
		return Object.values(classes).flat();
	}
	else return classes[supercategory];
};


function fetchBboxesAndSegments(imageID:number){
	const bboxes = [];
	const segments = [];

	const annotationForImage = instancesVal.annotations.filter(annotation => annotation.image_id === imageID);
	annotationForImage.forEach((annotation)=>{
		bboxes.push({box: annotation.bbox, category: instancesVal.categories.find(item=> item.id ===annotation.category_id).name});
		segments.push({area: annotation.area, segments: annotation.segmentation, category: instancesVal.categories.find(item=> item.id ===annotation.category_id).name        })
	});
	return {bboxes: bboxes, segments: segments};

}


export const getRandomCocoExample = ()=>{

	const random = Math.round(Math.random() * (instancesVal.images.length));
	const image = instancesVal.images[random];

	return {
		filename: image.file_name,
		url: image.coco_url,
		height: image.height,
		width: image.width,
		info: fetchBboxesAndSegments(image.id)
	}


}
