import React, {useState} from 'react';
import logo from './logo.svg';
import CarouselOfProjects from './components/CarouselOfProjects';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import ModalContent from './components/ModalContent';




function App() {

	const [modalContent, setModalcontent] = useState({
		tensorflow: false,
		arraySort: false,
		custom: false
	});


	function showModal(name: string){


		const items = modalContent;
		// 2. Make a shallow copy of the item you want to mutate
		let item = {...items[1]};
		// 3. Replace the property you're intested in
		item.name = 'newName';
		// 4. Put it back into our array. N.B. we *are* mutating the array here, but that's why we made a copy first
		items[1] = item;
		// 5. Set the state to our new copy
		this.setState({items});


		console.log('showMODAL ' + name);

		//setModalcontent(modalContent);
		setModalcontent({
			tensorflow: true,
			arraySort: false,
			custom: false
		});
	}

	function closeTf(){
		setModalcontent({
			tensorflow: false,
			arraySort: false,
			custom: false});
	}


	return (
		<div className="App">
			<ModalContent state={modalContent} onClose={closeTf}/>
			<CarouselOfProjects modalshow={showModal}/>
		</div>
	);
}

export default App;
