import React, {useState} from 'react';
import logo from './logo.svg';
import CarouselOfProjects from './components/CarouselOfProjects';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import ModalContent from './components/ModalContent';
import Header from './components/header';



function App() {

	const [modalContent, setModalcontent] = useState({
		tensorflow: false,
		arraySort: false,
		coco: false
	});


	function showModal(name: string){


		console.log('showMODAL ' + name);

		switch (name){
		case 'tensorflow':
			setModalcontent({tensorflow: true,arraySort: false, coco: false});
			break;
		case 'arraySort':
			setModalcontent({tensorflow: false,arraySort: true, coco: false});
			break;
		case 'coco':
			setModalcontent({tensorflow: false,arraySort: false, coco: true});
			break;
		}

	}

	function closeTf(){
		setModalcontent({
			tensorflow: false,
			arraySort: false,
			coco: false});
	}


	return (
		<div className="App">
			<Header/>
			<div id={'root'}>
				<ModalContent state={modalContent} onClose={closeTf}/>
				<CarouselOfProjects modalshow={showModal}/>
			</div>
		</div>
	);
}

export default App;
