import React, {Component, useState} from 'react';
import {Bar} from 'react-chartjs-2';
import {BubbleCommandsInterface, chartjsDataType, chartjsDataTemplate, initialBubbleCommandsState, BubbleSort} from '../interfaces/SortInterfaces';




export default class BubbleChart extends Component<any>{
	private chartReference: React.RefObject<any>;
	private chart: any;
	private timer: any;


	constructor(props: BubbleCommandsInterface) {
		super(props);

		this.state = chartjsDataTemplate;

		this.chartReference = React.createRef<any>();

		this.timer =1;

	}

	bubbleSort() {
		const labels = this.chart.data.labels;
		const data = this.chart.data.datasets[0].data;
		const colors = this.chart.data.datasets[0].backgroundColor;
		let swapped;
		let timeout = 0;
		do {
			swapped = false;
			for (let i = 0; i < data.length; i++) {
				if (data[i] > data[i + 1]) {
					this.swap(labels, i);
					this.swap(data, i);
					this.swap(colors, i);
					timeout += 100;
					this.updateChartDelayed(labels.slice(0), data.slice(0), colors.slice(0), timeout);
					swapped = true;
				}
			}
		} while (swapped);
	}

	swap(arr:any, i:any) {
		const tmp = arr[i];
		arr[i] = arr[i + 1];
		arr[i + 1] = tmp;
	}

	componentDidMount(){
		this.chart = this.chartReference.current.chartInstance;

		const tempChartJSObject = chartjsDataTemplate;
		tempChartJSObject.datasets[0].label = this.props.commands.name;
		this.setState(tempChartJSObject);

		// setTimeout(() => {
		// 	this.bubbleSort();
		// }, 1000);

	}



	updateChartDelayed(labels: string[], data:number[], colors:string[], timeout:number) {
		this.timer = setTimeout(() => {
			this.chart.data.labels = labels;
			this.chart.data.datasets[0].data = data;
			this.chart.data.datasets[0].backgroundColor = colors;
			this.chart.update();
		}, timeout);
	}

	componentWillUnmount() {
		clearInterval(this.timer);
	}

	componentDidUpdate(prevProps:BubbleCommandsInterface) {

		//prevProps = initialBubbleCommandsState;
		if(this.props!= prevProps) {
			if(this.props.commands.startSorting) {
				// const temporaryDataToSort = chartjsDataTemplate;
				// temporaryDataToSort.datasets[0].data = BubbleSort({data:chartjsDataTemplate});
				// this.setState(temporaryDataToSort);
				// this.forceUpdate();
				setTimeout(() => {
					this.bubbleSort();
				}, 500);


			}
			if(this.props.commands.arrayLength!==prevProps.commands.arrayLength){
				// const temporaryDataToSort = chartjsDataTemplate;
				// temporaryDataToSort.datasets[0].data = Array.from({length: this.props.commands.arrayLength}, () => Math.floor(Math.random() * 1000));
				// temporaryDataToSort.labels = Array.from({ length: this.props.commands.arrayLength }, (_, i) => (i+1).toString());
				// this.setState(temporaryDataToSort);
			}

		}

	}




	render(){
		return(
			<div><Bar data={this.state} width={100} height={50} ref={this.chartReference}/> </div>
		);

	}


}
