import {AnyAction} from 'redux';

export function sortReducer(state: number | number[] | undefined, action :{type: string}){
    
	if (action.type === 'REASSIGN'){
		return state.push(1);
	}else if(action.type === 'POP'){
		return  state.pop();
	}

	return state;
}
