import {AnyAction} from 'redux';

export const sort = 'sort';


const initialState = [0];

export function sortReducer(state= initialState, action :{type: string}):number[]{
    
	if (action.type === randomizeArray){
		state.push(1);
		return state;
	}else if(action.type === 'POP'){
		state.pop();
		return state;
	}

	return state;
}
