import React,{Component} from 'react';
import {Badge, Form} from 'react-bootstrap';
import {classes, parseSubCategories} from './cocoParser';
import {Typeahead} from 'react-bootstrap-typeahead';

interface subClassesStateInterface {
	items: string[];
}

export class ClassesCOCO extends Component{
	public state: subClassesStateInterface;
	public superCategoriesRef = React.useRef<any>();

	constructor(props:any) {
		super(props);
		this.state = {items: ['']};

	}


	appendSubclasses(e:string[]){
		//e.preventDefault();
		if(!this.state?.items.includes(e[0]) && e[0]!== undefined) {
			const prevSubsState = this.state;
			prevSubsState?.items.push(e[0]);
			this.setState(prevSubsState);
			console.log(this.state);
		}

	}

	render(){

		return(
			<Form>
				<Form.Group controlId="formBasicClasses">
					<p>Superclasses: </p>
					<Form.Control size="sm" as="select" ref={this.superCategoriesRef}>
						<option value='0'>all</option>
						{
							Object.keys(classes).map((item:any,it:number)=>{

								return <option key={it+1} value={it+1}>{item}</option>;

							})
						}
						)
					</Form.Control>
					<p></p>
					<p>Subclasses: </p>

				</Form.Group>

				<Typeahead
					id="toggle-example"
					options={Object.keys(classes)}
					onChange={(e)=>{ this.appendSubclasses(e);} }
					placeholder="Subclasses...">
				</Typeahead>
				<br></br>
				{
					this.state?.items.map((item,it)=>{

						return <Badge key={it} variant="success">{item}</Badge>;
					})
				}

				<br></br>
				<br></br>
				<br></br>
				<br></br>
				<br></br>
				<br></br>
				<br></br>
				<br></br>
				<br></br>
				<br></br>
				<br></br>
				<br></br>
				<br></br>
				<br></br>
				<br></br>
			</Form>
		);

	}
}
