import React, {useState} from 'react';
import logo from './logo.svg';
import CarouselOfProjects, {projectStore} from './components/CarouselOfProjects';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import ModalContent from './components/ModalContent';
import {arraySort, custom, tensorflow} from './redux/carouselReducer';




function App() {

	const [carouselItemState, setCarouselItemState] = useState({
		tensorflow:false,
		arraySort:false,
		custom:false
	});

	projectStore.subscribe(()=>{

		const name = projectStore.getState();

		if(name===tensorflow) {
			console.log(name);
			setCarouselItemState({
				tensorflow:true,
				arraySort:false,
				custom:false
			});
		}else if(name===arraySort){
			setCarouselItemState({
				tensorflow:false,
				arraySort:true,
				custom:false
			});
		}else if(name===custom){
			setCarouselItemState({
				tensorflow:false,
				arraySort:false,
				custom:true
			});
		}
		setTimeout(()=>{
			console.log('FIRED ACTION IS ,',carouselItemState);
		},1000);
	});




	return (
		<div className="App">
			<ModalContent data={carouselItemState}/>
			<CarouselOfProjects />
		</div>
	);
}

export default App;
