import React, {Component} from 'react';
import {Button} from  'react-bootstrap';
import {BubbleCommandsInterface} from '../interfaces/SortInterfaces';


interface submitBtn {
    data:{
        className:string,
        content: string
    }
}

export class BubbleSortButton extends Component<submitBtn>{

	constructor(props: submitBtn) {
		super(props);

	}


	componentDidUpdate(prevProps:submitBtn) {
		if(this.props.data.content!==prevProps.data.content){
			this.props.data.content = '1';
		}

	}

	render(){
		return(
			<div><Button variant="success" type="submit" className={this.props.data.className}>{this.props.data.content}</Button> </div>
		);
	}


}
