import React,{Component} from 'react';
import {Badge, Form} from 'react-bootstrap';
import {classes, parseSubCategories} from './cocoParser';
import {Typeahead} from 'react-bootstrap-typeahead';

interface subClassesStateInterface {
	subclasses: string[],
	badges: string[];
}

export class ClassesCOCO extends Component{
	public state: subClassesStateInterface;
	public superCategoriesRef = React.createRef<any>();

	constructor(props:any) {
		super(props);
		this.state = {subclasses: [''], badges: ['']};
	}


	componentDidMount() {
		this.setState({items: parseSubCategories('all'), badges: ['']});
	}

	appendSubclasses(){
		const superClassValue = this.superCategoriesRef.current.value;
		this.setState({items: parseSubCategories(superClassValue)});
	}

	render(){

		return(
			<Form>
				<Form.Group controlId="formBasicClasses">
					<p>Superclasses: </p>
					<Form.Control size="sm" as="select" ref={this.superCategoriesRef} onChange={(e)=>{ this.appendSubclasses();} }>
						<option value='all'>all</option>
						{
							Object.keys(classes).map((item:any,it:number)=>{

								return <option key={it+1} value={item}>{item}</option>;

							})
						}
						)
					</Form.Control>
					<p></p>
					<p>Subclasses: </p>

				</Form.Group>

				<Typeahead
					id="toggle-example"
					options={this.state.items}
					placeholder="Subclasses...">
				</Typeahead>
				<br></br>
				{/*{*/}
				{/*	this.state?.items.map((item,it)=>{*/}

				{/*		return <Badge key={it} variant="success">{item}</Badge>;*/}
				{/*	})*/}

				{/*}*/}

				<br></br>
				<br></br>
				<br></br>
				<br></br>
				<br></br>
				<br></br>
				<br></br>
				<br></br>
				<br></br>
				<br></br>
				<br></br>
				<br></br>
				<br></br>
				<br></br>
				<br></br>
			</Form>
		);

	}
}
