import React from 'react';
import {randomColor} from '../interfaces/SortInterfaces';
interface bbox{
	bbox: number[],
	category:string
}
interface segment{
	segments: number[][]
}
interface segments{
	area: number,
	segments:segment,
	category:string
}



interface props{
	filename: string,
	url: string,
	height: number,
	width: number,
	info: {
		bboxes: bbox[],
		segments: segments

	}
}

export class DrawCOCOimage extends React.Component<any, any>{
	public canvasRef = React.createRef<any>();
	public canvas:any;
	constructor(props: any) {
		super(props);

	}

	componentDidMount() {
		//console.log('qweqwe     ', this.props.props.Image.width);
		const imageObj1 = new Image();
		imageObj1.src = this.props.props.Image.url;
		imageObj1.onload = ()=> {
			const ctx = this.canvas.getContext('2d');
			ctx.drawImage(imageObj1,0,0);
			//ctx.fillStyle = 'rgba(21,235,21,0.5)';
			//ctx.fillRect(25,72,32,32);
			this.props.props.Image.info.bboxes.forEach( (bbox: any)=>{
				ctx.fillStyle = `rgba(${randomColor()}, ${randomColor()}, ${randomColor()}, 0.3`;
				ctx.fillRect(bbox.box[0],bbox.box[1],bbox.box[2],bbox.box[3]);

			});

		};
	}


	render() {
		return (
			<div>
				<canvas ref={(canvas)=>this.canvas=canvas} width={this.props.props.Image.width} height={this.props.props.Image.height}> </canvas>
			</div>
		);

	}
}
