import React from 'react';
import {randomColor} from '../interfaces/SortInterfaces';
interface bbox{
	bbox: number[],
	category:string
}
interface segment{
	segments: number[][]
}
interface segments{
	area: number,
	segments:segment,
	category:string
}



interface props{
	filename: string,
	url: string,
	height: number,
	width: number,
	info: {
		bboxes: bbox[],
		segments: segments

	}
}
interface stateInterface{
	bboxes:number[][], descriptions:string[], colors:string[]
}

export class DrawCOCOimage extends React.Component<any, any>{
	public canvasRef = React.createRef<any>();
	public canvas:any;
	public canvasForeground:any;
	readonly state:stateInterface;
	constructor(props: any) {
		super(props);
		this.state = { bboxes:[], descriptions:[], colors:[]};

	}

	componentDidMount() {
		//console.log('qweqwe     ', this.props.props.Image.width);
		this.updateImage();
	}


	handleMouse(e:any){

		const ctx = this.canvas.getContext('2d');
		const rect = this.canvas.getBoundingClientRect();
		const x = e.clientX - rect.left;
		const y = e.clientY - rect.top;
		let i = 0;
		let r:number[];
		ctx.clearRect(0, 0, e.target.width, e.target.height);

		while (i< this.state.bboxes.length) {
			r = this.state.bboxes[i++];
			ctx.beginPath();
			ctx.rect(r[0], r[1], r[2], r[3]);
			ctx.fillStyle = ctx.isPointInPath(x, y) ? 'blue':'yellow';
			ctx.fill();
		}
	}



	updateImage(){
		const bboxes:number[]=[];
		const descriptions:string[] = [];
		const colors:string[] = [];
		const imageObj1 = new Image();
		console.log(this.props.props.Image.url);
		imageObj1.src = this.props.props.Image.url;
		imageObj1.onload = ()=> {
			const ctx = this.canvas.getContext('2d');
			ctx.drawImage(imageObj1,0,0);
			//ctx.fillStyle = 'rgba(21,235,21,0.5)';
			//ctx.fillRect(25,72,32,32);
			this.props.props.Image.info.bboxes.forEach( (bbox: any)=>{
				const fillColor = `rgba(${randomColor()}, ${randomColor()}, ${randomColor()}, 0.3`;
				bboxes.push(bbox.box);
				descriptions.push(bbox.category);
				colors.push(fillColor);
				ctx.fillStyle = fillColor;
				ctx.fillRect(bbox.box[0],bbox.box[1],bbox.box[2],bbox.box[3]);

			});

		};
		this.setState({bboxes: bboxes as number[], descriptions: descriptions});
	}

	componentDidUpdate(prevProps: Readonly<any>, Props: Readonly<any>) {
		if(this.props!==prevProps){
			this.updateImage();


		}
	}




	render() {
		return (
			<div>
				<canvas ref={(canvas)=>this.canvasForeground=canvas} width={this.props.props.Image.width} height={this.props.props.Image.height}></canvas>
				<canvas style={{zIndex:2, marginTop:'-220px'}} onMouseMove={this.handleMouse.bind(this)}  ref={(canvas)=>this.canvas=canvas} width={this.props.props.Image.width} height={this.props.props.Image.height}> </canvas>
			</div>
		);

	}
}
