import {AnyAction} from 'redux';

export const bubbleSort = 'bubblesort';
export const bubbleSortCanceled = 'bubblesortCanceled';
export const arraySort = 'arraySort';
export const custom= 'custom';

const initialState = '';

export function SortingStateReducer(state= initialState, action :{type: string}):string{


	switch (action.type) {

	case bubbleSort:
		if(state==bubbleSort){
			state = bubbleSortCanceled;
		}
		state=bubbleSort;
		break;
	case arraySort:
		state = arraySort;
		break;
	case custom:
		state = custom;
		break;
	default:
		state = '';
		break;
	}

	return state;
}
