import React from 'react';
//import * as React from 'react';
import {CommandsInterface, randomColor} from '../interfaces/SortInterfaces';
import {OverlayTrigger, Tooltip} from 'react-bootstrap';
import {ReactDOM} from 'react';

interface bbox{
	box: number[],
	category:string
}
interface segment{
	segments: number[]
}

interface segments{
	area: number,
	segments: number[][],
	category:string
}


interface props{
	props: {
		Image:{
		filename: string,
		url: string,
		height: number,
		width: number,
		info: {
			bboxes: bbox[],
			segments: segments[]
		},
		showBboxes: boolean,
		showKeypoints: boolean
	}
	}
}
interface stateInterface{
	bboxes:number[][], segments: number[][], descriptions:string[]
}



export class DrawCOCOimage extends React.Component<props, any>{
	public canvas:any;
	public canvasForeground:any;
	private imageHasBeenLoaded: boolean;
	public tooltipRef:any;
	private data:stateInterface;
	private hColors: string[];
	constructor(props: any) {
		super(props);
		this.hColors = Array.from({length: 150}, () => `rgba(${randomColor()}, ${randomColor()}, ${randomColor()}, 0.4`);
		this.tooltipRef = React.createRef();
		this.imageHasBeenLoaded = false;
		this.data = { bboxes:[], segments:[], descriptions:[]};

	}

	componentDidMount() {
		this.updateImage();
	}


	handleMouse(e:any){

		const ctx = this.canvasForeground.getContext('2d');
		const rect = this.canvasForeground.getBoundingClientRect();
		const x = e.clientX - rect.left;
		const y = e.clientY - rect.top;
		let i = 0;
		let r:number[];
		if(this.props.props.Image.showBboxes && this.imageHasBeenLoaded) {
			ctx.clearRect(0, 0, e.target.width, e.target.height);

			while (i < this.data.bboxes.length) {
				r = this.data.bboxes[i];
				ctx.beginPath();
				ctx.rect(r[0], r[1], r[2], r[3]);
				const [R, G, B] = this.hColors[i].slice(5, this.hColors[i].length).split(',');
				ctx.fillStyle = ctx.isPointInPath(x, y) ? `rgba(${R},${G},${B},0.6)` : this.hColors[i];

				if (ctx.isPointInPath(x, y)) {
					try {
						this.tooltipRef.current.innerText = this.props.props.Image.info.bboxes[i].category;
					}catch (e){
						console.log('Maybe we can find category for chosen segment');
					}
				}
				ctx.fill();
				i++;
			}
		}
	}


	fillWithKeypoints(){
		const ctxForeground = this.canvasForeground.getContext('2d');

		this.props.props.Image.info.segments.forEach((segment, index)=>{
			//console.log(segment.segments);
			//console.log('--------------', segment.segments[0][0], '---------------');
			try {
				ctxForeground.beginPath();
				ctxForeground.moveTo(segment.segments[0][0], segment.segments[0][1]);
				for (let i = 2; i < segment.segments[0].length - 1; i += 2) {

					ctxForeground.lineTo(segment.segments[0][i], segment.segments[0][i + 1]);

				}
				ctxForeground.lineTo(segment.segments[0][0], segment.segments[0][1]);
				ctxForeground.lineWidth = 8;
				ctxForeground.stroke();
				ctxForeground.fillStyle = this.hColors[index];
				ctxForeground.fill();
				ctxForeground.beginPath();
			}catch (e) {
				console.log(e);
			}
		});

	}


	updateImage(){
		this.imageHasBeenLoaded = false;
		const bboxes:number[][]=[];
		const descriptions:string[] = [];
		const segments: number[]=[];

		const imageObj1 = new Image();
		//console.log(this.props.props.Image.url);
		imageObj1.src = this.props.props.Image.url;
		imageObj1.onload = ()=> {
			const ctx = this.canvas.getContext('2d');
			const ctxForeground = this.canvasForeground.getContext('2d');
			ctx.drawImage(imageObj1,0,0);

			if(this.props.props.Image.showBboxes) {
				ctxForeground.clearRect(0,0,this.props.props.Image.width,this.props.props.Image.height);
				this.props.props.Image.info.bboxes.forEach((bbox: any, index) => {
					const fillColor = this.hColors[index];
					//console.log(colors[index]);
					bboxes.push(bbox.box);
					descriptions.push(bbox.category);
					ctxForeground.fillStyle = fillColor;
					ctxForeground.fillRect(bbox.box[0], bbox.box[1], bbox.box[2], bbox.box[3]);

				});

				if(this.data.segments.length===0) {
					this.data = {
						bboxes: bboxes as number[][],
						segments: [],
						descriptions: descriptions,
					};
				}else {
					this.data = {bboxes:this.data.bboxes, segments: this.data.segments, descriptions: this.data.descriptions};
				}


			}else{
				ctxForeground.clearRect(0,0,this.props.props.Image.width,this.props.props.Image.height);
			}

			if(this.props.props.Image.showKeypoints){
				this.fillWithKeypoints();
				// this.props.props.Image.info.segments.forEach((segment: any, index) => {
				// 	//console.log(1);
				// 	const fillColor = this.hColors[index];
				// 	segments.push(segment.segments);
				// 	this.fillWithKeypoints();
				// });

			}

			this.imageHasBeenLoaded = true;
			try {
				this.tooltipRef.current.innerText = '';
			}catch (e){console.log(''); }
		};
	}


	componentDidUpdate(prevProps:props) {
		//

		if(this.props.props.Image.showBboxes===true){
			//console.log('On');
			this.updateImage();
		}else{
			//console.log('Off');
			this.updateImage();
		}

		if(this.props.props.Image.showKeypoints===true){
			//console.log('segments true');
			this.updateImage();
		}else{
			//console.log('segments false');
			this.updateImage();
		}

	}




	render() {
		return (
			<div>

				<OverlayTrigger placement={'bottom'} overlay={ <Tooltip id="tooltip-disabled"><i ref={this.tooltipRef}> </i></Tooltip> } >
					<div style={{ display: 'flex'}}>
						<canvas style={{zIndex:1}}   ref={(canvas)=>this.canvas=canvas} width={this.props.props.Image.width} height={this.props.props.Image.height}> </canvas>
						<canvas style={{top: 0,zIndex:2, marginLeft:`-${this.props.props.Image.width}px`}} onMouseMove={this.handleMouse.bind(this)} ref={(canvas)=>this.canvasForeground=canvas} width={this.props.props.Image.width} height={this.props.props.Image.height}></canvas>
					</div>
				</OverlayTrigger>
			</div>
		);

	}
}
