import React, {useState} from 'react';
import logo from './logo.svg';
import CarouselOfProjects from './components/CarouselOfProjects';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import ModalContent from './components/ModalContent';




function App() {

	const [modalContent, setModalcontent] = useState({
		tensorflow: false,
		arraySort: false,
		custom: false
	});


	function showModal(name: string){


		console.log('showMODAL ' + name);

		switch (name){
		case 'tensorflow':
			setModalcontent({tensorflow: true,arraySort: false,custom: false});
			break;
		case 'arraySort':
			setModalcontent({tensorflow: false,arraySort: true,custom: false});
			break;
		case 'custom':
			setModalcontent({tensorflow: false,arraySort: false,custom: true});
			break;
		}

	}

	function closeTf(){
		setModalcontent({
			tensorflow: false,
			arraySort: false,
			custom: false});
	}


	return (
		<div className="App">
			<ModalContent state={modalContent} onClose={closeTf}/>
			<CarouselOfProjects modalshow={showModal}/>
		</div>
	);
}

export default App;
