import React, {useRef} from 'react';
import {Modal, Button, Container, Row, Col, Form} from 'react-bootstrap';
import {Bar} from 'react-chartjs-2';
import {MathComponent} from 'mathjax-react';
import {createStore} from 'redux';
import {Provider} from 'react-redux';
import {XYPlot, XAxis, YAxis, HorizontalGridLines, LineSeries} from 'react-vis';
import {projectStore} from './CarouselOfProjects';
import {tensorflow, arraySort, custom} from '../redux/carouselReducer';



type onclose = ()=>void;

interface stateObj{
	data: {
		tensorflow: boolean,
		arraySort: boolean,
		custom: boolean,
	}

}

const style = {
	large:
		{
			maxWidth: '1600px'
		}
};

let typeToShow:string;

type showState = {
	tensorflow:boolean,
	arraySort:boolean,
	custom:boolean
};



export default class ModalContent extends React.Component<any,showState> {
	data:any;

	constructor(props: stateObj) {
		super(props);

		this.state= {
			tensorflow:tr,
			arraySort:false,
			custom:false
		};

	}

	handleClose = (name:string) => {
		projectStore.dispatch({type:''});

	}
	handleShow = (name: string) => {
		projectStore.getState()===tensorflow? this.setState({
			tensorflow:true,
			arraySort:false,
			custom:false
		}) :this.setState({
			tensorflow:false,
			arraySort:false,
			custom:false
		});
		console.log('WTFFFFFFFFF');
		return true;
	}


	render() {
		return (
			<>
				<Modal
					name='tensorflow'
					show={this.state.tensorflow}
					onHide={this.handleClose}
					style={style.large}
					dialogClassName="modal-dialog modal-xl"
					aria-labelledby="example-custom-modal-styling-title"
				>
					<Modal.Header closeButton>
						<Modal.Title id="example-custom-modal-styling-title">
							TensorflowJS Modal Styling
						</Modal.Title>
					</Modal.Header>
					<Modal.Body>
						<p>
							Ipsum molestiae natus adipisci modi eligendi? Debitis amet quae unde
							commodi aspernatur enim, consectetur. Cumque deleniti temporibus
							ipsam atque a dolores quisquam quisquam adipisci possimus
							laboriosam. Quibusdam facilis doloribus debitis! Sit quasi quod
							accusamus eos quod. Ab quos consequuntur eaque quo rem! Mollitia
							reiciendis porro quo magni incidunt dolore amet atque facilis ipsum
							deleniti rem!
						</p>
					</Modal.Body>
				</Modal>

				<Modal
					show={false}
					onHide={this.handleClose}
					style={style.large}
					dialogClassName="modal-dialog modal-xl"
					aria-labelledby="example-custom-modal-styling-title"
				>
					<Modal.Header closeButton>
						<Modal.Title id="example-custom-modal-styling-title">
							ArraySort Modal Styling
						</Modal.Title>
					</Modal.Header>
					<Modal.Body className="show-grid">
						<Container>
							<Row xs={2} md={14} lg={15}>
								<Col>
									<h3>Bubble sort</h3>
								</Col>
							</Row>

							<p>Bubble sort, sometimes referred to as sinking sort, is a simple sorting algorithm that
								repeatedly steps through the list, compares adjacent elements and swaps them if they are
								in the wrong order. The pass through the list is repeated until the list is sorted.</p>
							<p>This simple algorithm performs poorly in real world use and is used primarily as an
								educational tool.</p>

							<Row>
								<Col xs={6} md={4}>
									<h5>Complexity:</h5>
									<p style={{marginLeft: '-4rem'}}><MathComponent tex={String.raw`O(n^2)`}/></p>
								</Col>
								<Col xs={6} md={4}>
									<h5>Visualization</h5>
									{/*<BubbleChart array={arrayToSort.datasets[0].data} refresh={createDataArrayForSort}*/}
									{/*			 ReassignArray={true} State={{data: testArray}}/>*/}
									{/*<Bar key='1' data={arrayToSort} width={150} height={80} redraw={true} ref={test}/>*/}

								</Col>

								<Col xs={6} md={4}>
									<h5 style={{marginLeft: '4rem'}}>Panel</h5>
									{/*<Form style={{marginLeft: '4rem'}} onSubmit={handleBubbleSortSubmit}>*/}
									{/*	<Form.Group>*/}
									{/*		<Form.Label className="my-1 mr-2" htmlFor="inlineFormCustomSelectPref">*/}
									{/*			Number of elements to sort:*/}
									{/*		</Form.Label>*/}
									{/*		<Form.Control size="sm" as="select" ref={bubbleInputValue}>*/}
									{/*			<option value='10'>10</option>*/}
									{/*			<option value='50'>50</option>*/}
									{/*			<option value='100'>100</option>*/}
									{/*			<option value='200'>200</option>*/}

									{/*		</Form.Control>*/}
									{/*	</Form.Group>*/}
									{/*	<Button variant="success" type="submit" block>*/}
									{/*		Sort!*/}
									{/*	</Button>*/}
									{/*</Form>*/}
								</Col>
							</Row>
						</Container>

						{/*Chart*/}

						<p>
							Ipsum molestiae natus adipisci modi eligendi? Debitis amet quae unde
							commodi aspernatur enim, consectetur. Cumque deleniti temporibus
							ipsam atque a dolores quisquam quisquam adipisci possimus
							laboriosam. Quibusdam facilis doloribus debitis! Sit quasi quod
							accusamus eos quod. Ab quos consequuntur eaque quo rem! Mollitia
							reiciendis porro quo magni incidunt dolore amet atque facilis ipsum
							deleniti rem!
						</p>
					</Modal.Body>
				</Modal>

				<Modal
					show={false}
					onHide={this.handleClose}
					style={style.large}
					dialogClassName="modal-dialog modal-xl"
					aria-labelledby="example-custom-modal-styling-title"
				>
					<Modal.Header closeButton>
						<Modal.Title id="example-custom-modal-styling-title">
							Custom Modal Styling
						</Modal.Title>
					</Modal.Header>
					<Modal.Body>
						<p>
							Ipsum molestiae natus adipisci modi eligendi? Debitis amet quae unde
							commodi aspernatur enim, consectetur. Cumque deleniti temporibus
							ipsam atque a dolores quisquam quisquam adipisci possimus
							laboriosam. Quibusdam facilis doloribus debitis! Sit quasi quod
							accusamus eos quod. Ab quos consequuntur eaque quo rem! Mollitia
							reiciendis porro quo magni incidunt dolore amet atque facilis ipsum
							deleniti rem!
						</p>
					</Modal.Body>
				</Modal>

			</>
		);
	}
}
