import React from 'react';
import {Modal} from 'react-bootstrap';
import {txtDetectionState} from '../interfaces/stateInterfaces';
import {loadGraphModel} from '@tensorflow/tfjs';

export class TextDetection extends React.Component<any, txtDetectionState>{
	private canva: any;
	private imageLoaderConfigs:any;
	constructor(props:any) {
		super(props);
		this.state;

	}


	loadCanvas(){
		const ctx = this.canva.getContext('2d');
		const img = new Image();
		img.src = './COCO_train2014_000000004172.jpg';
		img.onload = ()=>{
			ctx.drawImage(img,0,0);
		};
	}

	async load_model() {
		// It's possible to load the model locally or from a repo
		// You can choose whatever IP and PORT you want in the "http://127.0.0.1:8080/model.json"     just set it before in your https server
		const model = await loadGraphModel("http://127.0.0.1:8080/model.json");
		//const model = await loadGraphModel("https://raw.githubusercontent.com/hugozanini/TFJS-object-detection/master/models/web_model/model.json");
		return model;
	}

	componentDidMount() {
		this.loadCanvas();
	}


	render() {
		return(
			<div className={'tfTxtContainer'}>
				<div id={'imagesContainer'}>

				</div>

				<div id={'tfTxtWorkspace'}>
					<div id={'activeImage'}>
						<canvas width={640} height={427} ref={(canvas)=>this.canva=canvas} id={'input-canvas'}> </canvas>
					</div>
				</div>
			</div>
		);
	}
}
