import React, {useState, createContext} from 'react';
import logo from './logo.svg';
import CarouselOfProjects from './components/CarouselOfProjects';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import ModalContent from './components/ModalContent';
import Header from './components/header';
import Particles from 'react-particles-js';
import {backgroundParticlesConfig} from './components/configs/particlesConfig';
import {ThemeContext} from './components/context/themeContext';

function App() {

	const [modalContent, setModalcontent] = useState({
		tensorflow: false,
		arraySort: false,
		coco: false
	});


	function showModal(name: string){
		switch (name){
		case 'tensorflow':
			setModalcontent({tensorflow: true,arraySort: false, coco: false});
			break;
		case 'arraySort':
			setModalcontent({tensorflow: false,arraySort: true, coco: false});
			break;
		case 'coco':
			setModalcontent({tensorflow: false,arraySort: false, coco: true});
			break;
		}
	}

	function closeTf(){
		setModalcontent({
			tensorflow: false,
			arraySort: false,
			coco: false});
	}



	return (
		<ThemeContext.Provider>
			<div className="App">
				<Header/>
				<div id={'contentBody'}>
					<div id={'carouselColContainer'} style={{display: 'inline-block'}}>
						<Particles  params={JSON.parse(JSON.stringify(backgroundParticlesConfig)) }/>
						<ModalContent state={modalContent} onClose={closeTf}/>
						<CarouselOfProjects modalshow={showModal}/>
					</div>
				</div>
			</div>
		</ThemeContext.Provider>
	);
}

export default App;
