import React, {useState, createContext} from 'react';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import CarouselOfProjects from './components/CarouselOfProjects';
import ModalContent from './components/ModalContent';
import Header from './components/header';
import Particles from 'react-particles-js';
import {backgroundParticlesConfig} from './components/configs/particlesConfig';
import {ThemeContext} from './components/context/themeContext';

function App() {

	const [modalContent, setModalcontent] = useState({
		tensorflow: false,
		arraySort: false,
		coco: false
	});

	const [mainTheme, setTheme] = useState({theme: 'night'});


	function showModal(name: string){
		switch (name){
		case 'tensorflow':
			setModalcontent({tensorflow: true,arraySort: false, coco: false});
			break;
		case 'arraySort':
			setModalcontent({tensorflow: false,arraySort: true, coco: false});
			break;
		case 'coco':
			setModalcontent({tensorflow: false,arraySort: false, coco: true});
			break;
		}
	}

	function changeTheme(){
		setTheme((prevTheme)=> prevTheme.theme==='night'? {theme: 'day'}: {theme: 'night'});
		console.log(mainTheme.theme);
	}


	function closeTf(){
		setModalcontent({
			tensorflow: false,
			arraySort: false,
			coco: false});
	}



	return (
		<ThemeContext.Provider value={{
			theme: mainTheme.theme,
		}}>
			<div className="App">
				<Header callback={changeTheme} />

				<div id={'contentBody'} className={mainTheme.theme}>
					<div id={'carouselColContainer'} style={{display: 'inline-block'}}>
						<Particles params={JSON.parse(JSON.stringify(backgroundParticlesConfig(mainTheme.theme) )) }/>
						<ModalContent state={modalContent} onClose={closeTf}/>
						<CarouselOfProjects modalshow={showModal}/>
					</div>
				</div>
			</div>
		</ThemeContext.Provider>
	);
}

export default App;
