import {AnyAction} from 'redux';

export const bubbleSort = 'bubblesort';
export const bubbleSortCancelled = 'bubblesortCancelled';
export const heapSort = 'heapSort';
export const heapSortCancelled = 'heapSortCancelled';
export const quickSort = 'quickSort';
export const quickSortCancelled = 'quickSortCancelled';

export const arraySort = 'arraySort';
export const custom= 'custom';


const initialState = {
	bubble:'',
	heap:'',
	quick:''
};

export type storeCommands = {
	bubble:string,
	heap:string,
	quick:string
}



export function SortingStateReducer(state= initialState, action :{type: string}):storeCommands{


	switch (action.type){

	case bubbleSort:
		if(state.bubble === bubbleSort){
			state = {bubble: bubbleSortCancelled, heap: state.heap, quick: state.quick};
		}else state = {bubble: bubbleSort, heap: state.heap, quick: state.quick};
		break;
	case heapSort:
		if(state.heap === heapSort){
			state = {bubble: state.bubble, heap: heapSortCancelled, quick: state.quick};
		} else state = {bubble: state.bubble, heap: heapSort, quick: state.quick};
		break;
	case quickSort:
		if(state.heap === heapSort){
			state = {bubble: state.bubble, heap: state.heap, quick: quickSortCancelled};
		} else state = {bubble: state.bubble, heap: state.heap, quick: quickSort};
		break;
	}



	return state;
}
