import {AnyAction} from 'redux';

export const tensorflow = 'tensorflow';
export const arraySort = 'arraySort';
export const custom= 'custom';


type returnObject = {
	tensorflow:boolean,
	arraySort:boolean,
	custom:boolean
};

const initialState = '';



export function carouselReducer(state= initialState, action :{type: string}):string{
    
	
	switch (action.type) {
	case tensorflow:
		state = {tensorflow: true, arraySort: false, custom: false};
		break;
	case arraySort:
		state = {tensorflow: false, arraySort: true, custom: false};
		break;
	case custom:
		state = {tensorflow: false, arraySort: false, custom: true};
		break;
	default:
		state = {tensorflow: false, arraySort: false, custom: false};
		break;
	}

	return state;
}
