import React from 'react';
import {Modal} from 'react-bootstrap';
import {txtDetectionState} from '../interfaces/stateInterfaces';
import { Tensor, InferenceSession} from 'onnxjs';
import loadImage from 'blueimp-load-image';


export class TextDetection extends React.Component<any, txtDetectionState>{
	private imageLoaderConfigs: any;
	constructor(props:any) {
		super(props);
		this.state;
		this.imageLoaderConfigs = {
			maxWidth: 256,
			maxHeight: 256,
			cover: true,
			crop: true,
			canvas: true,
			crossOrigin: 'Anonymous',
		};
	}


	renderImage(img: Event | HTMLImageElement | HTMLCanvasElement) {
		if ((img as Event).type === 'error') {
			return;
		}
		try {
			const element = document.getElementById('input-canvas') as HTMLCanvasElement;
			const ctx = element.getContext('2d');
			ctx!.drawImage(img as HTMLImageElement, 0, 0);
		} catch (e) {
			console.error(e);
		}
	}

	loadImageToCanvas(url: string) {
		if (!url) {
			return;
		}
		loadImage(url, this.renderImage, this.imageLoaderConfigs);
	}


	async start(){

		const session = new InferenceSession();
		// const session = new InferenceSession({ backendHint, Profiler });
		const url = 'E:\\PROJCT\\CMDER\\volf_server\\east_model.onnx';
		await session.loadModel(url);
		// Load image.
		const imageLoader = new ImageLoader(777, 1000);
		const imageData = await imageLoader.getImageData('E:\\PROJCT\\CMDER\\volf_server\\00040534.png');
	}

	componentDidMount() {
		this.start();
	}


	render() {
		return(
			<div className={'tfTxtContainer'}>
				<div id={'imagesContainer'}>

				</div>

				<div id={'tfTxtWorkspace'}>
					<div id={'activeImage'}>
						<canvas id={'input-canvas'}> </canvas>
					</div>
				</div>
			</div>
		);
	}
}
