import React, {Component, useState} from 'react';
import {Bar} from 'react-chartjs-2';
import {BubbleCommandsInterface, chartjsDataType, chartjsDataTemplate, initialBubbleCommandsState} from '../interfaces/SortInterfaces';
import {sortingStore} from './ModalContent';
import {bubbleSort, bubbleSortCanceled} from '../redux/SortingStateReducer';

type callback=()=>void;

export default class BubbleChart extends Component<BubbleCommandsInterface>{
	private chartReference: React.RefObject<any>;
	private chart: any;
	private timer: any;
	private isSorting: boolean;
	private interrupt: boolean;
	private uselessRefresh: boolean;
	public state;
	public props: any;
	private timeouts:number[];
	private callback : ()=>void;

	constructor(props: BubbleCommandsInterface) {
		super(props);
		this.state = chartjsDataTemplate;

		this.chartReference = React.createRef<any>();

		this.timer = 0;

		this.isSorting = false;

		this.interrupt = false;

		this.callback = this.props.callback;

		this.uselessRefresh = false;
		this.timeouts = [];

	}

	// randomize(size: number){
	// 	this.chart.data.labels = Array.from({length: size}, () => Math.floor(Math.random() * 1000));
	// 	this.chart.data.datasets[0].data = Array.from({ length: size }, (_, i) => (i+1).toString());
	// }

	bubbleSort() {
		const labels = this.chart.data.labels;
		const data = this.chart.data.datasets[0].data;
		const colors = this.chart.data.datasets[0].backgroundColor;
		let swapped;
		let timeout = 0;

		do {
			swapped = false;
			for (let i = 0; i < data.length; i++) {
				if (data[i] > data[i + 1]) {
					this.swap(labels, i);
					this.swap(data, i);
					this.swap(colors, i);
					timeout += 100;
					const theTimeout = this.updateChartDelayed(labels.slice(0), data.slice(0), colors.slice(0), timeout);
					this.timeouts.push(theTimeout);
					swapped = true;
				}
			}
		} while (swapped);

	}

	swap(arr:any, i:any) {
		const tmp = arr[i];
		arr[i] = arr[i + 1];
		arr[i + 1] = tmp;
	}

	equals = (a:number[], b:number[]) => JSON.stringify(a) === JSON.stringify(b);

	componentDidMount(){
		this.chart = this.chartReference.current.chartInstance;
		const tempChartJSObject = chartjsDataTemplate;
		tempChartJSObject.datasets[0].label = this.props.commands.name;
		this.setState(tempChartJSObject);
		console.log('did mount');
	}


	updateChartDelayed(labels: string[], data:number[], colors:string[], timeout:number) {

		this.timer = setTimeout(() => {
			this.isSorting = true;
			this.chart.data.labels = labels;
			this.chart.data.datasets[0].data = data;
			this.chart.data.datasets[0].backgroundColor = colors;
			this.chart.update();

			const eth = [...this.chart.data.datasets[0].data].sort((x,y)=> x-y);
			if( eth.toString() === this.chart.data.datasets[0].data.toString()) {
				sortingStore.dispatch({type:''});
				this.callback();
			}


		}, timeout);



		return this.timer;
	}

	componentWillUnmount() {
		clearInterval(this.timer);
		//sortingStore.dispatch({type:''});
	}


	clearAllTimeouts(){
		for(let timeout=0; timeout< this.timeouts.length; timeout++){
			clearInterval(this.timeouts[timeout]);
		}
		this.timeouts=[];
	}

	refreshChart(){
		const temporaryDataToSort = chartjsDataTemplate;
		temporaryDataToSort.datasets[0].data = Array.from({length: this.props.commands.arrayLength}, () => Math.floor(Math.random() * 1000));
		temporaryDataToSort.labels = Array.from({ length: this.props.commands.arrayLength }, (_, i) => (i+1).toString());
		this.setState(temporaryDataToSort);
	}

	componentDidUpdate(prevProps:BubbleCommandsInterface) {


		//console.log(`${JSON.stringify(this.props)},            ${JSON.stringify(prevProps)}`);

		if (!(JSON.stringify(prevProps) === JSON.stringify(this.props))) {
			console.log(this.timeouts);


			this.bubbleSort();

			this.uselessRefresh = this.equals(this.state.datasets[0].data, this.chart.data.datasets[0].data);




			if( sortingStore.getState()===bubbleSortCanceled || (this.props.commands.arrayLength !== prevProps.commands.arrayLength) ){
				this.clearAllTimeouts();
				this.refreshChart();
				//this.bubbleSort();
			}
			if(!this.uselessRefresh){
				console.log('NOT EQUAL!');
				this.bubbleSort();
			}

			// if(this.props.commands.refreshChart){
			// 	this.refreshChart();
			// }
			// setTimeout(() => {
			// 	this.bubbleSort();
			// }, 100);
			//
			//
			// this.equals(this.state.datasets[0].data, this.chart.data.datasets[0].data) ? this.uslessRefresh = true : this.uslessRefresh = false;
			//
			// if(!this.uslessRefresh){
			// 	console.log('test');
			// 	setTimeout(() => {
			// 		this.bubbleSort();
			// 	}, 100);
			// }
			//
			// if (this.props.commands.arrayLength !== prevProps.commands.arrayLength) {
			// 	this.refreshChart();
			//
			//
			// }


			// if (this.props.commands.arrayLength === prevProps.commands.arrayLength && !this.uslessRefresh) {
			// 	//this.refreshChart();
			// 	//sortingStore.dispatch({type:''});
			// 	//console.log('Is not sorting, refreshing chart...');
			// 	this.refreshChart();
			//
			// }
			// if (this.props.commands.arrayLength === prevProps.commands.arrayLength && !this.uslessRefresh) {
			// 	//console.log('Is sorting!!!...');
			//
			// 	//this.interrupt = true;
			// 	//clearInterval(this.timer);
			// 	//console.log(this.timer);
			//
			// }
			// if (this.props.commands.arrayLength !== prevProps.commands.arrayLength) {
			// 	if (!this.isSorting) {
			// 		// console.log(`Array sorting is ${this.isSorting}, refreshing...`);
			// 		// this.refreshChart();
			// 	}
			// }

		}


	}




	render(){
		return(
			<div><Bar data={this.state} width={100} height={50} ref={this.chartReference}/> </div>
		);

	}


}
