import React from 'react';
import {Modal} from 'react-bootstrap';
import {txtDetectionState} from '../interfaces/stateInterfaces';
import { Tensor, InferenceSession} from 'onnxjs';
import loadImage from 'blueimp-load-image';


export class TextDetection extends React.Component<any, txtDetectionState>{
	private canva: any;
	constructor(props:any) {
		super(props);
		this.state;
		// this.canva = React.createRef();
	}


	loadCanvas(){
		const ctx = this.canva.getContext('2d');
		const img = new Image();
		img.src = 'https://interactive-examples.mdn.mozilla.net/media/cc0-images/grapefruit-slice-332-332.jpg';
		img.onload = ()=>{
			ctx.drawImage(img,0,0);
		};
	}

	componentDidMount() {
		this.loadCanvas();
	}


	render() {
		return(
			<div className={'tfTxtContainer'}>
				<div id={'imagesContainer'}>

				</div>

				<div id={'tfTxtWorkspace'}>
					<div id={'activeImage'}>
						<canvas ref={(canvas)=>this.canva=canvas} id={'input-canvas'}> </canvas>
					</div>
				</div>
			</div>
		);
	}
}
