export const backgroundParticlesConfig ={

	particles: {
		number: {
			value: 90,
			density: {
				enable: true,
				value_area: 800
			}
		},
		color: {
			value: '#ffffff',
		},
		shape: {
			type: 'circle',
			stroke: {
				width: 0,
				color: '#000000'
			},
			polygon: {
				nb_sides: 3
			},
			image: {
				src: 'img/github.svg',
				width: 100,
				height: 100
			}
		},
		opacity: {
			value: 0.0,
			random: false,
			anim: {
				enable: false,
				speed: 1,
				opacity_min: 0.7,
				sync: false
			}
		},
		size: {
			value: 1,
			random: true,
			anim: {
				enable: false,
				speed: 40,
				size_min: 0.1,
				sync: false
			}
		},
		line_linked: {
			enable: true,
			distance: 250,
			color:'#ffffff',
			opacity: 0.3,
			width: 1.5
		},
		move: {
			enable: true,
			speed: 0.5,
			direction: 'left',
			random: false,
			straight: false,
			out_mode: 'out',
			bounce: false,
			attract: {
				enable: false,
				rotateX: 600,
				rotateY: 1200
			}
		}
	},
	interactivity: {
		detect_on: 'canvas',
		events: {
			onhover: {
				enable: false,
				mode: 'grab'
			},
			onclick: {
				enable: false,
				mode: 'push'
			},
			resize: true
		},
		modes: {
			grab: {
				distance: 140,
				line_linked: {
					opacity: 1
				}
			},
			bubble: {
				distance: 400,
				size: 40,
				duration: 2,
				opacity: 8,
			},
			repulse: {
				distance: 200,
				duration: 0.4
			},
			push: {
				particles_nb: 4
			},
			remove: {
				particles_nb: 2
			}
		}
	}

};
