import React, { Component } from 'react';
import { Bar } from 'react-chartjs-2';


interface propstosort {
	data: number;
	labels: number;
}

interface State  {
	labels:string[],
	datasets: [{
		label: string,
		data: number[],
		backgroundColor: string[],
		borderColor: string[],
		borderWidth: number
	}]
}




export default class BarChart extends Component<any,any> {

	constructor(props: propstosort) {
		super(props);

		this.state = {
			labels:Array.from({ length: 200 }, (_, i) => (i+1).toString()),
			datasets: [{
				label: '',
				data: Array.from({length: 200}, () => Math.floor(Math.random() * 1000)),
				backgroundColor: [
					'rgba(255, 99, 132, 0.2)',
					'rgba(54, 162, 235, 0.2)',
					'rgba(255, 206, 86, 0.2)',
					'rgba(75, 192, 192, 0.2)',
					'rgba(153, 102, 255, 0.2)',
					'rgba(255, 159, 64, 0.2)',
					'rgba(54, 162, 235, 0.2)',
					'rgba(255, 206, 86, 0.2)',
					'rgba(75, 192, 192, 0.2)',
					'rgba(153, 102, 255, 0.2)',

				],
				borderColor: [
					'rgba(255, 99, 132, 1)',
					'rgba(54, 162, 235, 1)',
					'rgba(255, 206, 86, 1)',
					'rgba(75, 192, 192, 1)',
					'rgba(153, 102, 255, 1)',
					'rgba(255, 159, 64, 1)',
					'rgba(54, 162, 235, 1)',
					'rgba(255, 206, 86, 1)',
					'rgba(75, 192, 192, 1)',
					'rgba(153, 102, 255, 1)'
				],
				borderWidth: 1
			}]
		};

	}

	componentDidMount() {
		setTimeout(() => {
			this.BubbleSort();
		}, 1000);
	}


	BubbleSort(){
		const data = this.state.datasets[0].data;
		let timeout = 100;
		for(let it=0; it<data.length; ++it){

			for(let that=0; that< data.length-1 -it;++that){
				if (data[that] > data[that+1]) {
					const temporary = data[that];
					data[that] = data[that+1];
					data[that+1] = temporary;

					//setArrayToSort({label: arrayToSort.label, labels:arrayToSort.labels, data:data});
					const temporaryState = this.state;

					temporaryState.datasets[0].data = data;
					timeout += 10;
					setTimeout(()=>{
						this.setState(temporaryState);
					}, timeout);


				}
			}
		}

		console.log(data);
	}



	render() {
		return (
			<Bar
				data={this.state}
				width={100}
				height={100}

			/>
		);
	}
}
