import React, { Component } from 'react';
import { Bar } from 'react-chartjs-2';


interface propstosort {
	data: number;
	labels: number;
	chartReference: any;
}

interface State  {
	labels:string[],
	datasets: [{
		label: string,
		data: number[],
		backgroundColor: string[],
		borderColor: string[],
		borderWidth: number
	}]
}




export default class BarChart extends Component<any,any> {
	chartReference: any;
	chart: any;
	timer: any;

	constructor(props: propstosort) {
		super(props);
		this.chartReference = React.createRef();

		this.state = {
			labels:Array.from({ length: 200 }, (_, i) => (i+1).toString()),
			datasets: [{
				label: '',
				data: Array.from({length: 200}, () => Math.floor(Math.random() * 1000)),
				backgroundColor: [
					'rgba(255, 99, 132, 0.2)',
					'rgba(54, 162, 235, 0.2)',
					'rgba(255, 206, 86, 0.2)',
					'rgba(75, 192, 192, 0.2)',
					'rgba(153, 102, 255, 0.2)',
					'rgba(255, 159, 64, 0.2)',
					'rgba(54, 162, 235, 0.2)',
					'rgba(255, 206, 86, 0.2)',
					'rgba(75, 192, 192, 0.2)',
					'rgba(153, 102, 255, 0.2)',

				],
				borderColor: [
					'rgba(255, 99, 132, 1)',
					'rgba(54, 162, 235, 1)',
					'rgba(255, 206, 86, 1)',
					'rgba(75, 192, 192, 1)',
					'rgba(153, 102, 255, 1)',
					'rgba(255, 159, 64, 1)',
					'rgba(54, 162, 235, 1)',
					'rgba(255, 206, 86, 1)',
					'rgba(75, 192, 192, 1)',
					'rgba(153, 102, 255, 1)'
				],
				borderWidth: 1
			}]
		};

	}

	componentDidMount() {
		this.chart = this.chartReference.current.chartInstance;
		setTimeout(() => {
			this.bubbleSort();
		}, 1000);
	}
	componentWillUnmount() {
		clearInterval(this.timer);
	}

	bubbleSort() {
		const labels = this.chart.data.labels;
		const data = this.chart.data.datasets[0].data;
		const colors = this.chart.data.datasets[0].backgroundColor;
		let swapped;
		let timeout = 0;
		do {
			swapped = false;
			for (let i = 0; i < data.length; i++) {
				if (data[i] > data[i + 1]) {
					this.swap(labels, i);
					this.swap(data, i);
					this.swap(colors, i);
					timeout += 100;
					this.updateChartDelayed(labels.slice(0), data.slice(0), colors.slice(0), timeout);
					swapped = true;
				}
			}
		} while (swapped);
	}

	swap(arr:number[], i:number) {
		const tmp = arr[i];
		arr[i] = arr[i + 1];
		arr[i + 1] = tmp;
	}

	updateChartDelayed(labels:string[], data:number[], colors:string[], timeout:any) {
		this.timer = setTimeout(() => {
			this.chart.data.labels = labels;
			this.chart.data.datasets[0].data = data;
			this.chart.data.datasets[0].backgroundColor = colors;
			this.chart.update();
		}, timeout);
	}


	render() {
		return (
			<Bar
				ref={this.chartReference}
				data={this.state}
				width={100}
				height={100}

			/>
		);
	}
}
