import React, {Component} from 'react';
import {Button, Col, Container, Form, Modal, Row, Badge} from 'react-bootstrap';
import {Figure, Image, Accordion, Card} from 'react-bootstrap';
import {DrawCOCOimage} from './drawCOCOimage';
import {getRandomCocoExample, getFilteredCocoExample} from './cocoParser';

import {ClassesCOCO, filterData} from './classesCOCO';

export function COCO () {

	const [randomCOCOimage, setRandomCOCOimage] = React.useState({Image: getRandomCocoExample()});
	const imageRef = React.useRef<any>();
	const bboxCheckbox = React.useRef<any>();
	const segmentCheckbox = React.useRef<any>();

	function changeImage(filter?: filterData){ //function that changes image in viewport
		if(filter){
			const filteredExample = getFilteredCocoExample(filter);
			bboxCheckbox.current.checked = filteredExample.showBboxes;
			segmentCheckbox.current.checked = filteredExample.showKeypoints;
			imageRef.current.style.pointerEvents = 'none';
			setTimeout(() => {
				imageRef.current.style.pointerEvents = 'auto';
			}, 500);

			setRandomCOCOimage({Image: filteredExample});

			//console.log('with filter');
		}else {
			imageRef.current.style.pointerEvents = 'none';
			setTimeout(() => {
				imageRef.current.style.pointerEvents = 'auto';
			}, 500);
			//style={ {pointerEvents: 'none'} }
			const newRandom = getRandomCocoExample();
			newRandom.showBboxes = randomCOCOimage.Image.showBboxes;
			newRandom.showKeypoints = randomCOCOimage.Image.showKeypoints;
			setRandomCOCOimage({Image: newRandom});
		}
	}

	function showbboxes(){

		const tempRandomState = randomCOCOimage;
		tempRandomState.Image.showBboxes = !tempRandomState.Image.showBboxes;
		setRandomCOCOimage({Image:tempRandomState.Image});

		console.log('set state');


	}


	function showKeypoints(){
		const tempRandomState = randomCOCOimage;
		tempRandomState.Image.showKeypoints = !tempRandomState.Image.showKeypoints;
		setRandomCOCOimage({Image:tempRandomState.Image});
		console.log('set keypoinst');
	}


	return(

		<Container>
			<Row>
				<Col xs={12} md={8}>
					<div>
						<Form>
							<Form.Check style={{float:'left', marginLeft: '.5rem'}}
								//checked
								ref={bboxCheckbox}
								onChange={()=>showbboxes()}
								type="switch"
								id="bboxes"
								label="Show bounding boxes"
							/>
							<Form.Check style={{float:'left', marginLeft: '.5rem'}}
								ref={segmentCheckbox}
								onChange={()=>showKeypoints()}
								type="switch"
								id="keypoints"
								label="Show keypoints"
							/>
						</Form>
					</div>
					<Figure ref={imageRef} onClick={()=>changeImage()}>
						<DrawCOCOimage props={randomCOCOimage}/>
						<Figure.Caption>
							Created sample
						</Figure.Caption>
					</Figure>
				</Col>
				<Col xs={6} md={4}>

					<ClassesCOCO callback={changeImage}></ClassesCOCO>

				</Col>
			</Row>

			<br></br>
			<br></br>
			<Row>
				<Col xs={6} md={14}>
					<h6>Default export format:</h6>

					<Col style={{color:'red', fontWeight: 'bold' }}>
						{'{'}
						<Col><Row style={{marginLeft:'.5rem', color:'black', fontWeight: 'bold' }}>filename: <text style={{color: 'green', fontWeight: 'lighter'}}> 0000000XXXXX.jpg</text><text style={{color: 'black', fontWeight: 'bold'}}>,</text></Row></Col>
						<Col><Row style={{marginLeft:'.5rem', color:'black', fontWeight: 'bold' }}>path: <text style={{color: 'green', fontWeight: 'lighter'}}> C:\\path\\to\\image\\0000000XXXXX.jpg</text><text style={{color: 'black', fontWeight: 'bold'}}>,</text></Row></Col>
						<Col><Row style={{marginLeft:'.5rem', color:'black', fontWeight: 'bold' }}>url: <text style={{color: 'green', fontWeight: 'lighter'}}> http://path/to/image/0000000XXXXX.jpg</text><text style={{color: 'black', fontWeight: 'bold'}}>,</text></Row></Col>
						<Col><Row style={{marginLeft:'.5rem', color:'black', fontWeight: 'bold' }}>width: <text style={{color: 'green', fontWeight: 'lighter'}}> 640 </text><text style={{color: 'black', fontWeight: 'bold'}}>,</text></Row></Col>
						<Col><Row style={{marginLeft:'.5rem', color:'black', fontWeight: 'bold' }}>height: <text style={{color: 'green', fontWeight: 'lighter'}}> 640 </text><text style={{color: 'black', fontWeight: 'bold'}}>,</text></Row></Col>
						<Col><Row style={{marginLeft:'.5rem', color:'black', fontWeight: 'bold' }}>license: <text style={{color: 'green', fontWeight: 'lighter'}}> type-of-license-for-related-img </text><text style={{color: 'black', fontWeight: 'bold'}}>,</text></Row></Col>
						<Col><Row style={{marginLeft:'.5rem', color:'black', fontWeight: 'bold' }}>info: </Row></Col>
					</Col>


				</Col>
				<Col xs={6} md={4}>
						.col-xs-6 .col-md-4
				</Col>
				{/*<Col xs={6} md={4}>*/}
				{/*		.col-xs-6 .col-md-4*/}
				{/*</Col>*/}
			</Row>
		</Container>
	);


}
